    <div class=\"bg-white rounded-2xl shadow-lg overflow-hidden\">
        {% if hotel.imageHotel %}
            <img src=\"{{ asset(hotel.imageHotel) }}\"
                 alt=\"Image de {{ hotel.libelleHotel }}\"
                 class=\"w-full h-64 object-cover\">
        {% endif %}

        <div class=\"p-6\">
            <h1 class=\"text-3xl font-bold text-[#244D61] mb-4\">
                {{ hotel.libelleHotel }}
            </h1>

            <p class=\"text-gray-700 text-lg leading-relaxed\">
                {{ hotel.descriptionHotel }}
            </p>

            <div class=\"mt-6\">
                <p class=\"text-sm text-gray-500\">Biome : {{ hotel.biome.libelleBiome }}</p>
            </div>
           
        </div>
    </div>
</section>
{% endblock %}
